package com.prashant.demo;

public class Array1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A[] = {2,4,6,8,10};
		for(int i=0;i<A.length;i++)
		{
			System.out.print(A[i]+" ");
		}
		for(int i=A.length-1;i>=0;i--)
		{
			System.out.println(A[i]);
			
		}
	}

}
